syntax = "proto3";

option go_package = "/proto";


//进入游戏
message m_game_2_enter_game_tos{
  optional int32  game_id = 1;           //0大厅1龙虎2红黑百家乐
  optional int32  room    = 2;         	 //房间
  optional int32  desk    = 3;           //桌子
}
message m_game_2_enter_game_toc{
  optional int32  game_id = 1;           //0大厅1龙虎2红黑百家乐
  optional int32  room    = 2;         	 //普通房间就是房间ID
  optional int32  desk    = 3;           //桌子
}

// 服务端回给客户端当前游戏的信息   server--> client
message m_game_hong_hei_2_info_toc{
  optional  string  s_id             = 1;                  // 玩家Id
  optional  string  nickname         = 2;                  // 玩家昵称
  optional  float   coin             = 3;                  // 玩家金币
  repeated p_game_hong_hei_2_sessions  sessions = 4;                  // 场次 1平民  2小资  3老板  4土豪 后期后台配置
}

message p_game_hong_hei_2_sessions{
  optional  int32    min_bet       = 1;        // 最小投注金额   // TODO 后台配置
  optional  int32    max_bet       = 2;        // 最大投注金额
  optional  int32    min_bet_lucky = 3;        // 幸运一击最小投注金额
  optional  int32    max_bet_lucky = 4;        // 幸运一击最大投注金额
}

// 玩家选择场次  C--> S
message m_player_into_game_hong_hei_2_tos{
  optional  string  s_id     = 1;                          // 玩家Id
  optional  int32   sessions = 2;                          // 场次 1平民  2小资  3老板  4土豪
}

//// 验证资产   S--> C
//message m_verify_coin_hong_hei_2_toc{
//  optional  string  s_id             = 1;                  // 玩家Id
//  optional  bool    enough           = 2;                  // 资产是否足够  true 足够   false 资产不足
//}


// 进入场次返回所有桌子的信息  S--> C
message m_all_desk_hong_hei_2_info_toc{
  optional string          s_id            = 1;    // 玩家id
  optional string          nickname        = 2;    // 玩家名称
  optional float           coin            = 3;    // 金币
  optional bool            enough          = 4;    // 验证资产是否足够   True 足够  False 不足
  repeated p_all_desk_hong_hei_2_info desk            = 5;    // 所有桌子信息
}

// 当前场次内所有牌桌的信息
message p_all_desk_hong_hei_2_info{
  optional int32     desk_id    = 1;           // 桌子Id
  optional int32     status     = 2;           // 当前桌子的状态  1未下注  2下注中  3开牌中  4结算中
  optional int32     next_time  = 3;           // 下次状态变化的时间
  optional int32     set        = 4;           // 进行到第几局
  // TODO 已开牌的结果  人数...
}

// 游戏服主动推送状态的改变
message m_desk_status_change_toc{
  optional int32 desk_id     = 1;  // 桌子Id
  optional int32 status      = 2;  // 当前的状态
  optional int32 next_status = 3;  // 即将变化的状态
}


// 玩家进入桌子 C-->S
message m_player_into_desk_hong_hei_2_tos{
  optional string      s_id            = 1;    // 玩家id
  optional string      nickname        = 2;    // 玩家名称
  optional int32       desk_id         = 3;    // 桌子号
}

// 返回当前桌子信息  S--> C
message m_desk_info_toc{
  optional string               s_id            = 1;    // 玩家id
  optional string               nickname        = 2;    // 玩家名称
  optional float                coin            = 3;    // 我的金币
  optional int32                desk_id         = 4;    // 桌子号
  optional int32                status          = 5;	  // 当前桌子的状态
  optional int32                next_time       = 6;    // 下一个状态变化的时间
  optional int32                set             = 7;    // 第几局
  repeated p_desk_info_hong_hei_2_history  history         = 8;    // 当前桌子的输赢记录  保留n条
  repeated p_desk_bet_hong_hei_2_area      bet_area        = 9;    // 投注区域信息
  repeated p_desk_my_bet_hong_hei_2_info   my_bet          = 10;   // 我的投注信息
}

// 当前桌子的输赢记录
message p_desk_info_hong_hei_2_history{
  optional int32   set  = 1;         // 第几局
  optional int32   win  = 2;         // 哪一方赢了
  optional int32   px   = 3;         // 牌型
}

// 投注区域信息
message p_desk_bet_hong_hei_2_area{
  optional int32                area         = 1;          // 哪个区域   红 黑 幸运一击
  optional int32                all_bet      = 2;          // 当前区域所有的筹码
  optional int32                red_ood      = 3;          // 红方的赔率
  optional int32                black_ood    = 4;          // 黑方的赔率
  repeated p_desk_ood_lucky_hong_hei_2     ood_lucky    = 5;          // 幸运一击的赔率
}

// 幸运一击赔率
message p_desk_ood_lucky_hong_hei_2{
  optional int32  px   = 1;    // 牌型
  optional int32  ood  = 2;    // 赔率
}

// 当前牌桌我的投注信息
message p_desk_my_bet_hong_hei_2_info{
  optional int32 area   = 1;     // 投注区域
  optional int32 my_bet = 2;     // 我的投注
}

// 玩家投注 C--> S
message m_player_bet_hong_hei_2_tos{
  optional int32  area    = 1;     // 投注区域
  optional int32  nums    = 2;     // 投注数量
  optional int32  session = 3;     // 场次
  optional int32  desk_id = 4;     // 桌号
  optional string s_id    = 5;     // 玩家id
  optional int32  set     = 6;     // 局数
}

// 返回玩家下注信息
message m_player_bet_hong_hei_2_toc{
  optional int32   area = 1;									// 下注区域
  optional int64   chip = 2;									// 下注金额
  optional int64   my_all_chip = 3;					  // 该区域我自己的下注总额
  optional int64   my_chip = 4;								// 我自己剩下的筹码
}

// 游戏结果 S--> C
message m_game_result_hong_hei_2_toc{
  optional string s_id        = 1;     // 玩家id
  optional int32  desk_id     = 2;     // 桌子号
  optional int32  win         = 3;     // 胜利方
  optional int32  win_px      = 4;     // 胜利方牌型
  repeated int32  p_win_cards = 5;     // 胜利方牌组
  optional int32  lose        = 6;     // 输方
  optional int32  lose_px     = 7;     // 输方牌型
  repeated int32  p_lose_cards= 8;     // 输方牌组
}

// 游戏结算  S--> C
message m_game_settlement_hong_hei_2_toc{
  optional int32  bet_area = 1;    // 我的投注区域
  optional bool   win      = 2;    // 输或者赢
  optional float  nums     = 4;    // 数量
}
